      cmd.ServerFrame))0 {

      if(hit.count > 0) {
        var hit = hits[0];
        var targetEntity = hit.body.GetComponent<BoltEntity>();

        if(targetEntity.StateIs<ILivingEntity>()) {
          targetEntity.GetState<ILivingEntity>().Modify().HP -= activeWeapon.damage; 
        }
      }
    }
  }
}

